import logo from './logo.svg';
import './App.css';

function App() {

  const currentDate = new Date();
  const months = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December',
  ];

  const month = mocurrentDate.getMonth();
  const day = currentDate.getDate();
  const year = currentDate.getFullYear();
  const formattedDate = `${month} ${day}, ${year}`


  return (
    <div className="App">
      <div className='container'>

        <h1 className='container_date'>{formattedDate}</h1>
        <div className='weather_data'>
          <h2 className='container_city'>Indonesia</h2>
          <img className='container_img' scr='./thunder.png' width="180px" alt='thunder'/>
          <h2 className='container_degree'>34.22</h2>
          <h2 className='country_per'>sunny</h2>
          <form className='form'>
            <input type='text' className='input' placeholder='Enter city name'></input>
            <button type='submit'>Get</button>
          </form>

        </div>

      </div>
    </div>
  );
}

export default App;
